'use client';
'use strict';

var context = require('@chakra-ui/utils/context');

const [PopoverProvider, usePopoverContext] = context.createContext({
  name: "PopoverContext",
  errorMessage: "usePopoverContext: `context` is undefined. Seems you forgot to wrap all popover components within `<Popover />`"
});
const [PopoverStylesProvider, usePopoverStyles] = context.createContext({
  name: `PopoverStylesContext`,
  errorMessage: `usePopoverStyles returned is 'undefined'. Seems you forgot to wrap the components in "<Popover />" `
});

exports.PopoverProvider = PopoverProvider;
exports.PopoverStylesProvider = PopoverStylesProvider;
exports.usePopoverContext = usePopoverContext;
exports.usePopoverStyles = usePopoverStyles;
